mapper.select:
SELECT 

FROM q_materials a

WHERE a.id = #{id}

SELECT
FROM q_materials a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id = #{id}
AND (m.id = #{qModel.id} OR m.parent_ids LIKE '%'||#{qModel.id}||'%' '%'+#{qModel.id}+'%' CONCAT('%',#{qModel.id},'%'))
AND a.quality = #{quality}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.create_by LIKE '%'||#{createBy.id}||'%' '%'+#{createBy.id}+'%' concat('%',#{createBy.id},'%')
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM q_materials a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE q_materials SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
